required:
(function() {
  if (wasCollected && hasNoProtocolDeviation === null && specimenType !== null) {
    if (specimenType.name === "EDTA-Blood/DNA") {
      return "Genomic blood sample: Blood collection used an EDTA-blood tubes. Immediately after blood collection, the tube was swirled upside down 8-10 times (180°). For direct shipment, storage at room temperature, or alternatively storage at -80°C until shipment.";
    }
    if (specimenType.name === "EDTA-Tube/Plasma") {
      return "Metabolomic plasma sample: Blood collection used an EDTA-blood tube. Immediately after blood draw, the tubes was inverted 8-10 times to mix samples. Within 30 minutes of blood draw, centrifuge samples at 4°C, 2,000 x g for 10 min, then immediately transferred  into cryovials and -80 ºC freezer storage.";
    }
    if (specimenType.name === "PAXgene-RNA") {
      return "Transcriptomic blood sample: Blood collection used a PAXgene-RNA tube. Immediately after blood draw, invert the tube gently 8-10 times to mix samples. Incubation of the tubes occurred at room temperature, tubes upright, for between 2 hours to max. 4 hours. For direct shipment, storage at room temperature. Alternatively, freezer storage at -20 ºC for up to 24hours, and then -80 ºC until shipment.";
    }
    if (specimenType.name === "Urine") {
      return "Metabolomic urine sample: Urine was placed at 2-8 ºC within 10 minutes after collection, kept at 2-8 ºC for a maximum of 2 hours, centrifugation at 2 - 8 ºC, 1000-3000 x g, 5 min, then immediately transferred into cryovials and -80 ºC freezer storage.";
    }
  }
})();

